INSERT INTO `jobs` (`id`,`position_name`,`organization_title`,`organization_url`,`job_url`,`location`,`labor_function`,`posted_date`,`logo_url`,`description`) VALUES (85,'Graphic Designer','See more open positions at scale.jobs','https://jobs.techstars.com/companies/scale-jobs-2#content','https://jobs.techstars.com/companies/scale-jobs-2/jobs/55073828-graphic-designer','Hyderabad, Telangana, India; Telangana, India','design',1753401600,'https://cdn.getro.com/companies/801251bb-d70b-5dd0-95c8-d56d764bdcae-1748880942','<p><strong>Location:</strong> Hyderabad in Office, WFH opportunity based on performance <br><strong>Shift:</strong> Dayshift with Flexible hours <br><strong>Employment Type:</strong> Full-time <br><strong>Salary:</strong> As per market standards<br><strong>Experience:</strong> 2-5 years</p><h3><strong>About Us</strong></h3><p><a href="http://Scale.jobs" rel="noopener noreferrer nofollow">Scale.jobs</a> is revolutionizing the job application process by providing human-powered assistance to job seekers in the US market. Our unique approach combines human expertise with smart technology to help our clients land their dream jobs.</p><h3><strong>Role Overview </strong></h3><p>We''re seeking a creative Graphic Designer to join our growing team. In this role, you''ll be responsible for creating visually appealing content for social media platforms and developing engaging designs that help showcase our clients'' professional profiles.</p><h3><strong>Key Responsibilities</strong></h3><ul> <li><p>Design and create eye-catching social media graphics and templates</p></li> <li><p>Develop visual content for various platforms including Instagram, Facebook, and LinkedIn</p></li> <li><p>Create consistent branding materials and graphics for our company</p></li> <li><p>Collaborate with the video team to ensure visual consistency across all content</p></li> <li><p>Stay updated with current design trends and best practices</p></li> </ul><h3><strong>Required Qualifications</strong></h3><ul> <li><p>Experience in creating social media designs </p></li> <li><p>Proficiency in Adobe Creative Suite (Photoshop, Illustrator) or similar tools</p></li> <li><p>Strong understanding of design principles and typography</p></li> <li><p>Experience with social media content creation and dimensions</p></li> <li><p>Ability to work quickly while maintaining attention to detail</p></li> <li><p>Good eye for composition and color theory</p></li> <li><p>Knowledge of current design trends and social media aesthetics</p></li> <li><p>Basic understanding of video editing is a plus</p></li> </ul>');
